DROP DATABASE IF EXISTS `supermarket_sales_data`;
CREATE DATABASE `supermarket_sales_data`; 
USE `supermarket_sales_data`;

SET NAMES utf8 ;
SET character_set_client = utf8mb4 ;

CREATE TABLE `items` (
  `item_id` bigint(15) NOT NULL,
  `item_name` varchar(512) NULL,
  `category_name` varchar(512) NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

